vite.config.ts.content:
import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import themePlugin from "@replit/vite-plugin-shadcn-theme-json";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    themePlugin(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist"),
    emptyOutDir: true,
  },
});

scripts/assign-subcategories-batch.ts.content:
/**
 * Script optimizat pentru atribuirea automată a subcategoriilor pe baza titlurilor produselor
 * Utilizează actualizări în masă pentru a îmbunătăți performanța
 */

import { db } from "../server/db";
import { products } from "../shared/schema";
import { eq, sql, inArray } from "drizzle-orm";

type SubCategoryMapItem = {
  keywords: string[];
  subCategory: string;
  priority?: number;
}

async function assignSubcategoriesBatch() {
  console.log("Început procesul de atribuire a subcategoriilor în lot...");

  // Lista de subcategorii cu cuvinte cheie și priorități
  const subCategoryMap: SubCategoryMapItem[] = [
    {
      subCategory: "T-Shirts",
      keywords: ["t-shirt", "tee", "short sleeve", "ss tee", "graphic tee", "printed tee"],
      priority: 1
    },
    {
      subCategory: "Shirts",
      keywords: ["shirt", "button up", "button-down", "oxford", "polo"],
      priority: 1
    },
    {
      subCategory: "Hoodies",
      keywords: ["hoodie", "zip up", "crewneck", "jumper", "pullover", "sweatshirt", "tech fleece"],
      priority: 2
    },
    {
      subCategory: "Jackets",
      keywords: ["jacket", "puffer", "fleece", "coat", "parka", "windbreaker", "down"],
      priority: 2
    },
    {
      subCategory: "Sweaters",
      keywords: ["sweater", "knit", "cardigan", "wool"],
      priority: 1
    },
    {
      subCategory: "Pants & Jeans",
      keywords: ["jeans", "pants", "trousers", "slacks", "cargo", "denim pants"],
      priority: 1
    },
    {
      subCategory: "Shorts",
      keywords: ["shorts", "mesh shorts", "sweat shorts"],
      priority: 2
    },
    {
      subCategory: "Tracksuits",
      keywords: ["tracksuit", "track suit", "track pants", "tech fleece", "full set"],
      priority: 3
    },
    {
      subCategory: "Vests",
      keywords: ["vest", "gilet", "sleeveless"],
      priority: 1
    },
    {
      subCategory: "Denim",
      keywords: ["denim", "denim jacket", "denim jeans", "raw denim"],
      priority: 1
    },
    {
      subCategory: "Vintage",
      keywords: ["vintage", "retro", "old school", "archive"],
      priority: 1
    },
    {
      subCategory: "Boxers",
      keywords: ["boxers", "underwear", "briefs", "trunks"],
      priority: 2
    },
    {
      subCategory: "Jerseys",
      keywords: ["jersey", "nba", "football jersey", "soccer jersey", "kit", "team jersey"],
      priority: 2
    },
    {
      subCategory: "Beanies",
      keywords: ["beanie", "knit hat", "winter hat"],
      priority: 1
    },
    {
      subCategory: "Socks",
      keywords: ["socks", "crew socks", "ankle socks"],
      priority: 1
    }
  ];

  // Reguli speciale de procesare
  const specialRules = [
    { keyword: "polo", isNotIn: ["shirt", "ralph lauren polo"], setTo: "Shirts" },
    { keyword: "zip up", isNotIn: [], setTo: "Hoodies" },
    { keyword: "crewneck", isNotIn: [], setTo: "Hoodies" },
    { keyword: "jumper", isNotIn: [], setTo: "Hoodies" },
    { keyword: "tech fleece", isNotIn: [], setTo: "Tracksuits" },
    { keyword: "fleece", isNotIn: ["tech fleece"], setTo: "Jackets" }
  ];

  // Obține toate produsele din categoria Clothing
  const clothingProducts = await db.select().from(products).where(eq(products.category, "Clothing"));

  console.log(`S-au găsit ${clothingProducts.length} produse în categoria Clothing.`);

  // Grupează produsele pe subcategorii
  const productSubCategories = new Map<number, string>();
  let updateCount = 0;

  for (const product of clothingProducts) {
    const title = product.title.toLowerCase();

    // Verifică dacă se aplică vreo regulă specială
    let assigned = false;

    // Procesează regulile speciale
    for (const rule of specialRules) {
      if (title.includes(rule.keyword.toLowerCase())) {
        // Verifică dacă NU conține vreunul din cuvintele cheie restrictive
        const hasRestriction = rule.isNotIn.some(forbidden => title.includes(forbidden.toLowerCase()));

        if (!hasRestriction) {
          productSubCategories.set(product.id, rule.setTo);
          assigned = true;
          break;
        }
      }
    }

    // Dacă nu a fost atribuit prin reguli speciale, verifică potrivirile normale
    if (!assigned) {
      let bestMatch: { subCategory: string, priority: number } | null = null;

      for (const subCatItem of subCategoryMap) {
        // Verifică dacă vreun cuvânt cheie din lista subcategoriei se regăsește în titlu
        const match = subCatItem.keywords.some(keyword => 
          title.includes(keyword.toLowerCase())
        );

        if (match) {
          // Păstrează subcategoria cu cea mai mare prioritate
          if (!bestMatch || (subCatItem.priority || 0) > (bestMatch.priority || 0)) {
            bestMatch = { 
              subCategory: subCatItem.subCategory, 
              priority: subCatItem.priority || 0 
            };
          }
        }
      }

      if (bestMatch) {
        productSubCategories.set(product.id, bestMatch.subCategory);
      }
    }
  }

  // Efectuează actualizările în masă
  for (const subCategory of new Set(productSubCategories.values())) {
    const productIds = [...productSubCategories.entries()]
      .filter(([_, sc]) => sc === subCategory)
      .map(([id, _]) => id);

    if (productIds.length > 0) {
      const chunkSize = 100; // Actualizează în loturi de 100 pentru a evita limitările BD

      for (let i = 0; i < productIds.length; i += chunkSize) {
        const chunk = productIds.slice(i, i + chunkSize);

        await db.update(products)
          .set({ subCategory: subCategory })
          .where(inArray(products.id, chunk));

        updateCount += chunk.length;
        console.log(`Actualizate ${chunk.length} produse cu subcategoria ${subCategory}`);
      }
    }
  }

  console.log(`Proces finalizat. ${updateCount} produse au fost actualizate cu subcategorii.`);
}

// Execută funcția și tratează erorile
assignSubcategoriesBatch()
  .then(() => {
    console.log("Script completat cu succes");
    process.exit(0);
  })
  .catch((error) => {
    console.error("Eroare la rularea scriptului:", error);
    process.exit(1);
  });

scripts/assign-subcategories-enhanced.ts.content:
/**
 * Script pentru atribuirea automată a subcategoriilor pe baza titlurilor produselor
 * Versiune îmbunătățită care acceptă subcategorii noi și reguli de prioritate
 */

import { db } from "../server/db";
import { products } from "../shared/schema";
import { eq, sql } from "drizzle-orm";

type SubCategoryMapItem = {
  keywords: string[];
  subCategory: string;
  priority?: number; // Prioritate mai mare înseamnă că are precedență în caz de potriviri multiple
}

async function assignSubcategoriesEnhanced() {
  console.log("Început procesul de atribuire a subcategoriilor îmbunătățit...");

  // Lista de subcategorii cu cuvinte cheie și priorități
  const subCategoryMap: SubCategoryMapItem[] = [
    {
      subCategory: "T-Shirts",
      keywords: ["t-shirt", "tee", "short sleeve", "ss tee", "graphic tee", "printed tee"],
      priority: 1
    },
    {
      subCategory: "Shirts",
      keywords: ["shirt", "button up", "button-down", "oxford", "polo"],
      priority: 1
    },
    {
      subCategory: "Hoodies",
      keywords: ["hoodie", "zip up", "crewneck", "jumper", "pullover", "sweatshirt", "tech fleece"],
      priority: 2 // Prioritate mai mare pentru "tech fleece" care ar putea fi și Tracksuit
    },
    {
      subCategory: "Jackets",
      keywords: ["jacket", "puffer", "fleece", "coat", "parka", "windbreaker", "down"],
      priority: 2
    },
    {
      subCategory: "Sweaters",
      keywords: ["sweater", "knit", "cardigan", "wool"],
      priority: 1
    },
    {
      subCategory: "Pants & Jeans",
      keywords: ["jeans", "pants", "trousers", "slacks", "cargo", "denim pants"],
      priority: 1
    },
    {
      subCategory: "Shorts",
      keywords: ["shorts", "mesh shorts", "sweat shorts"],
      priority: 2
    },
    {
      subCategory: "Tracksuits",
      keywords: ["tracksuit", "track suit", "track pants", "tech fleece", "full set"],
      priority: 3 // Prioritate mai mare pentru "tech fleece"
    },
    {
      subCategory: "Vests",
      keywords: ["vest", "gilet", "sleeveless"],
      priority: 1
    },
    {
      subCategory: "Denim",
      keywords: ["denim", "denim jacket", "denim jeans", "raw denim"],
      priority: 1
    },
    {
      subCategory: "Vintage",
      keywords: ["vintage", "retro", "old school", "archive"],
      priority: 1
    },
    {
      subCategory: "Boxers",
      keywords: ["boxers", "underwear", "briefs", "trunks"],
      priority: 2
    },
    {
      subCategory: "Jerseys",
      keywords: ["jersey", "nba", "football jersey", "soccer jersey", "kit", "team jersey"],
      priority: 2
    },
    {
      subCategory: "Beanies",
      keywords: ["beanie", "knit hat", "winter hat"],
      priority: 1
    },
    {
      subCategory: "Socks",
      keywords: ["socks", "crew socks", "ankle socks"],
      priority: 1
    }
  ];

  // Obține toate produsele din categoria Clothing care nu au o subcategorie asignată
  const clothingProducts = await db.select().from(products).where(eq(products.category, "Clothing"));

  console.log(`Found ${clothingProducts.length} products in the Clothing category.`);

  // Reguli speciale
  const specialRules = [
    { keyword: "polo", isNotIn: ["shirt", "ralph lauren polo"], setTo: "Shirts" },
    { keyword: "zip up", isNotIn: [], setTo: "Hoodies" },
    { keyword: "crewneck", isNotIn: [], setTo: "Hoodies" },
    { keyword: "jumper", isNotIn: [], setTo: "Hoodies" },
    { keyword: "tech fleece", isNotIn: [], setTo: "Tracksuits" },
    { keyword: "fleece", isNotIn: ["tech fleece"], setTo: "Jackets" }
  ];

  let updateCount = 0;
  let processedCount = 0;

  for (const product of clothingProducts) {
    processedCount++;

    if (processedCount % 100 === 0) {
      console.log(`Processed ${processedCount}/${clothingProducts.length} products...`);
    }

    const title = product.title.toLowerCase();

    // Verifică dacă se aplică vreo regulă specială
    let specialSubCategory = null;

    for (const rule of specialRules) {
      if (title.includes(rule.keyword.toLowerCase())) {
        // Verifică dacă NU conține vreunul din cuvintele cheie restrictive
        const hasRestriction = rule.isNotIn.some(forbidden => title.includes(forbidden.toLowerCase()));

        if (!hasRestriction) {
          specialSubCategory = rule.setTo;
          break;
        }
      }
    }

    if (specialSubCategory) {
      // Actualizează produsul cu subcategoria din regula specială
      await db.update(products)
        .set({ subCategory: specialSubCategory })
        .where(eq(products.id, product.id));

      updateCount++;
      continue; // Trecem la următorul produs
    }

    // Dacă nu există reguli speciale, verificăm potrivirile normale
    let bestMatch: { subCategory: string, priority: number } | null = null;

    for (const subCatItem of subCategoryMap) {
      // Verifică dacă vreun cuvânt cheie din lista subcategoriei se regăsește în titlu
      const match = subCatItem.keywords.some(keyword => 
        title.includes(keyword.toLowerCase())
      );

      if (match) {
        // Păstrează subcategoria cu cea mai mare prioritate
        if (!bestMatch || (subCatItem.priority || 0) > (bestMatch.priority || 0)) {
          bestMatch = { 
            subCategory: subCatItem.subCategory, 
            priority: subCatItem.priority || 0 
          };
        }
      }
    }

    if (bestMatch) {
      // Actualizează produsul cu subcategoria potrivită
      await db.update(products)
        .set({ subCategory: bestMatch.subCategory })
        .where(eq(products.id, product.id));

      updateCount++;
    }
  }

  console.log(`Proces finalizat. ${updateCount} produse au fost actualizate cu subcategorii.`);
}

// Execută funcția și tratează erorile
assignSubcategoriesEnhanced()
  .then(() => {
    console.log("Script completed successfully");
    process.exit(0);
  })
  .catch((error) => {
    console.error("Error running script:", error);
    process.exit(1);
  });

scripts/assign-subcategories-fast.ts.content:
import { db } from '../server/db';
import { products } from '../shared/schema';
import { sql } from 'drizzle-orm';

/**
 * Versiune optimizată a scriptului pentru atribuirea subcategoriilor
 * Folosește UPDATE-uri în lot pentru eficiență maximă
 */

async function assignSubcategoriesFast() {
  console.log('Început proces optimizat de atribuire a subcategoriilor...');

  // Definim subcategoriile și pattern-urile lor
  const subcategories = [
    { name: 'T-Shirts', pattern: '%(t-shirt|tshirt|t shirt|tee|tees)%' },
    { name: 'Shirts', pattern: '%(shirt|long sleeve|button|button up|button down|flannel|polo)%' },
    { name: 'Hoodies', pattern: '%(hoodie|hooded|pullover|sweatshirt)%' },
    { name: 'Jackets', pattern: '%(jacket|coat|parka|outerwear|bomber|puffer|windbreaker|varsity)%' },
    { name: 'Sweaters', pattern: '%(sweater|knit|jumper|crewneck|crew neck|cardigan|knitwear)%' },
    { name: 'Pants & Jeans', pattern: '%(pants|pant|trousers|jeans|cargos|cargo|slacks|joggers)%' },
    { name: 'Shorts', pattern: '%(shorts|short)%' },
    { name: 'Tracksuits', pattern: '%(tracksuit|track suit|sweatsuit|sweat suit|two piece|set|trackpants)%' },
    { name: 'Vests', pattern: '%(vest|gilet|sleeveless)%' },
    { name: 'Denim', pattern: '%(denim|jean|jeans)%' },
    { name: 'Vintage', pattern: '%(vintage|retro|old school)%' },
    { name: 'Boxers', pattern: '%(boxers|briefs|underwear|underpants|boxer shorts)%' }
  ];

  // Actualizează în lot pentru fiecare subcategorie
  for (const { name, pattern } of subcategories) {
    const result = await db.execute(
      sql`UPDATE products SET sub_category = ${name} 
          WHERE category = 'Clothing' 
          AND sub_category IS NULL 
          AND LOWER(title) LIKE ${pattern}`
    );

    console.log(`S-au clasificat ${result.rowCount || 0} produse în subcategoria "${name}"`);
  }

  // Verificare număr produse clasificate vs. neclasificate
  const [classifiedResult] = await db.select({ count: sql<number>`count(*)` })
    .from(products)
    .where(sql`category = 'Clothing' AND sub_category IS NOT NULL`);

  const [totalResult] = await db.select({ count: sql<number>`count(*)` })
    .from(products)
    .where(sql`category = 'Clothing'`);

  const classifiedCount = Number(classifiedResult?.count || 0);
  const totalCount = Number(totalResult?.count || 0);

  console.log(`Proces finalizat!`);
  console.log(`S-au clasificat ${classifiedCount} din ${totalCount} produse (${Math.round(classifiedCount/totalCount*100)}%).`);
  console.log(`${totalCount - classifiedCount} produse nu au putut fi clasificate automat.`);
}

// Execută funcția și gestionează închiderea conexiunii
assignSubcategoriesFast()
  .then(() => {
    console.log('Script executat cu succes!');
    process.exit(0);
  })
  .catch((error) => {
    console.error('Eroare la executarea scriptului:', error);
    process.exit(1);
  });

scripts/assign-subcategories-optimized.t.content:
import { db } from '../server/db';
import { products } from '../shared/schema';
import { sql } from 'drizzle-orm';

/**
 * Versiune optimizată a scriptului pentru atribuirea subcategoriilor
 * Folosește o singură interogare pentru fiecare subcategorie
 */

async function assignSubcategoriesOptimized() {
  console.log('Început proces optimizat de atribuire a subcategoriilor...');

  // Pentru fiecare subcategorie, definim un array de cuvinte cheie
  const subcategories = [
    { 
      name: 'T-Shirts', 
      keywords: ['t-shirt', 'tshirt', 't shirt', 'tee', 'tees', 'shirt tee'] 
    },
    { 
      name: 'Shirts', 
      keywords: ['shirt', 'long sleeve', 'button', 'button up', 'button down', 'flannel', 'polo'] 
    },
    { 
      name: 'Hoodies', 
      keywords: ['hoodie', 'hooded', 'pullover', 'sweatshirt'] 
    },
    { 
      name: 'Jackets', 
      keywords: ['jacket', 'coat', 'parka', 'outerwear', 'bomber', 'puffer', 'windbreaker', 'varsity'] 
    },
    { 
      name: 'Sweaters', 
      keywords: ['sweater', 'knit', 'jumper', 'crewneck', 'crew neck', 'cardigan', 'knitwear'] 
    },
    { 
      name: 'Pants & Jeans', 
      keywords: ['pants', 'pant', 'trousers', 'jeans', 'cargos', 'cargo', 'slacks', 'joggers'] 
    },
    { 
      name: 'Shorts', 
      keywords: ['shorts', 'short'] 
    },
    { 
      name: 'Tracksuits', 
      keywords: ['tracksuit', 'track suit', 'sweatsuit', 'sweat suit', 'two piece', 'set', 'trackpants'] 
    },
    { 
      name: 'Vests', 
      keywords: ['vest', 'gilet', 'sleeveless'] 
    },
    { 
      name: 'Denim', 
      keywords: ['denim', 'jean jacket', 'jean vest'] 
    },
    { 
      name: 'Vintage', 
      keywords: ['vintage', 'retro', 'old school'] 
    },
    { 
      name: 'Boxers', 
      keywords: ['boxers', 'briefs', 'underwear', 'underpants', 'boxer shorts'] 
    }
  ];

  let totalAssigned = 0;

  // Facem clasificarea pentru fiecare subcategorie folosind OR combinat pentru toate cuvintele cheie
  for (const { name, keywords } of subcategories) {
    try {
      // Construim condiția OR cu toate cuvintele cheie
      const conditionsSQL = keywords.map(keyword => 
        `LOWER(title) LIKE '%${keyword.toLowerCase()}%'`
      ).join(' OR ');

      // Executăm query-ul
      const result = await db.execute(
        sql`UPDATE products 
            SET sub_category = ${name} 
            WHERE category = 'Clothing' 
            AND sub_category IS NULL 
            AND (${sql.raw(conditionsSQL)})`
      );

      console.log(`S-au clasificat ${result.rowCount || 0} produse în subcategoria "${name}"`);
      totalAssigned += Number(result.rowCount || 0);
    } catch (error) {
      console.error(`Eroare la clasificarea subcategoriei "${name}":`, error);
    }
  }

  // Verificare număr produse clasificate vs. neclasificate
  const [classifiedResult] = await db.select({ count: sql<number>`count(*)` })
    .from(products)
    .where(sql`category = 'Clothing' AND sub_category IS NOT NULL`);

  const [totalResult] = await db.select({ count: sql<number>`count(*)` })
    .from(products)
    .where(sql`category = 'Clothing'`);

  const classifiedCount = Number(classifiedResult?.count || 0);
  const totalCount = Number(totalResult?.count || 0);

  console.log(`\nRezultat final:`);
  console.log(`Produse clasificate în această rulare: ${totalAssigned}`);
  console.log(`Total produse clasificate: ${classifiedCount} din ${totalCount} (${Math.round(classifiedCount/totalCount*100)}%).`);
  console.log(`${totalCount - classifiedCount} produse nu au putut fi clasificate automat.`);
}

// Execută funcția și gestionează închiderea conexiunii
assignSubcategoriesOptimized()
  .then(() => {
    console.log('\nScript executat cu succes!');
    process.exit(0);
  })
