async function fixShoeCategory() {
  console.log("Începerea procesului de corectare a categoriei Shoes...");

  try {
    // Pas 1: Citirea CSV-ului pentru a extrage titlurile exact
    const csvContent = fs.readFileSync('./attached_assets/brands shoes.csv', 'utf8');
    const records = parse(csvContent, {
      columns: true,
      skip_empty_lines: true
    });

    const exactShoeTitles = records.map((record: any) => record.title.trim());
    console.log(`S-au găsit ${exactShoeTitles.length} titluri exacte de încălțăminte în CSV.`);

    // Pas 2: Obține toate produsele din categoria Shoes în prezent
    const currentShoes = await db.select().from(products).where(eq(products.category, "Shoes"));
    console.log(`În prezent există ${currentShoes.length} produse în categoria Shoes.`);

    // Pas 3: Identifică produsele care trebuie să rămână în categoria Shoes
    const shoesToKeep = currentShoes.filter(product => {
      // Verifică dacă titlul produsului se potrivește exact cu unul din CSV
      const exactMatch = exactShoeTitles.some(title => 
        product.title.toLowerCase().trim() === title.toLowerCase().trim()
      );

      // Dacă nu avem o potrivire exactă, verificăm dacă începe cu unul din titlurile generice
      if (!exactMatch) {
        return shoeTitles.some(shoeTitle => 
          product.title.toLowerCase().startsWith(shoeTitle.toLowerCase())
        );
      }

      return exactMatch;
    });

    const shoesToKeepIds = shoesToKeep.map(shoe => shoe.id);
    console.log(`${shoesToKeepIds.length} produse vor rămâne în categoria Shoes.`);

    // Pas 4: Identifică produsele care trebuie mutate din Shoes în Clothing
    const shoesToMove = currentShoes.filter(product => !shoesToKeepIds.includes(product.id));
    console.log(`${shoesToMove.length} produse vor fi mutate din Shoes în Clothing.`);

    // Pas 5: Actualizează categoria produselor care trebuie mutate în Clothing
    if (shoesToMove.length > 0) {
      const shoesToMoveIds = shoesToMove.map(shoe => shoe.id);

      // Procesează în loturi pentru a evita limitele de dimensiune a query-urilor
      const batchSize = 100;
      for (let i = 0; i < shoesToMoveIds.length; i += batchSize) {
        const batch = shoesToMoveIds.slice(i, i + batchSize);

        await db.update(products)
          .set({ 
            category: "Clothing",
            // Resetăm subcategoria pentru a fi procesată de scriptul de subcategorii
            subCategory: null
          })
          .where(inArray(products.id, batch));

        console.log(`Mutate produsele ${i + 1} - ${i + batch.length} din ${shoesToMoveIds.length}.`);
      }
    }

    console.log("Procesul de corectare a categoriei Shoes a fost finalizat cu succes.");
    console.log(`În categoria Shoes au rămas ${shoesToKeepIds.length} produse.`);
    console.log(`${shoesToMove.length} produse au fost mutate în categoria Clothing.`);

  } catch (error) {
    console.error("A apărut o eroare în timpul procesului:", error);
  }
}

// Rulează scriptul
fixShoeCategory()
  .then(() => {
    console.log("Script finalizat.");
    process.exit(0);
  })
  .catch(error => {
    console.error("Eroare la rularea scriptului:", error);
    process.exit(1);
  });

scripts/fix-shoes-from-csv.ts.content:
/**
 * Script optimizat pentru asigurarea că toate produsele din CSV-ul specificat sunt în categoria Shoes
 * Folosim URL-urile ca identificator unic pentru găsirea produselor
 * Versiune optimizată care face update în lot pentru eficiență
 */

import { db } from "../server/db";
import { products } from "../shared/schema";
import { eq, or, and, inArray, notInArray, not, sql } from "drizzle-orm";
import * as fs from 'fs';
import { parse } from 'csv-parse/sync';

interface ProductFromCSV {
  title: string;
  price: string;
  image: string;
  buyUrl: string;
  viewUrl: string;
  category: string;
  featured: string;
}

async function fixShoesFromCSV() {
  console.log("Începerea procesului optimizat de corectare a categoriei pentru produsele din CSV...");

  try {
    // Citește fișierul CSV
    const csvContent = fs.readFileSync('./attached_assets/TJ JOYAGOO LINKS FOR WEBSITE - Export_Ready.csv', 'utf-8');
    const records: ProductFromCSV[] = parse(csvContent, {
      columns: true,
      skip_empty_lines: true
    });

    console.log(`Am găsit ${records.length} produse în CSV.`);

    // Extrage toate URL-urile din CSV
    const buyUrls = records.map(r => r.buyUrl);

    // Găsește toate produsele care au URL-urile din CSV
    const matchingProducts = await db.select()
      .from(products)
      .where(
        inArray(products.buyUrl, buyUrls)
      );

    console.log(`Am găsit ${matchingProducts.length} produse în baza de date care corespund cu CSV-ul.`);

    // Găsește produse care trebuie actualizate (nu sunt deja în categoria Shoes)
    const productsToUpdate = matchingProducts.filter(p => p.category !== "Shoes");

    console.log(`Produse de actualizat: ${productsToUpdate.length}`);

    if (productsToUpdate.length > 0) {
      // Obține ID-urile produselor care trebuie actualizate
      const productIdsToUpdate = productsToUpdate.map(p => p.id);

      // Actualizează toate produsele la categoria Shoes într-o singură operație
      await db.update(products)
        .set({
          category: "Shoes",
          subCategory: null
        })
        .where(inArray(products.id, productIdsToUpdate));

      console.log("Actualizare finalizată!");

      // Afișează detalii despre produsele actualizate pentru confirmare
      for (const product of productsToUpdate) {
        console.log(`Produs actualizat: "${product.title}" (ID: ${product.id}) de la categoria "${product.category}" la "Shoes"`);
      }
    }

    // Produse care erau deja în categoria corectă
    const alreadyInShoesCategory = matchingProducts.filter(p => p.category === "Shoes").length;

    // Produse care nu au fost găsite
    const notFoundUrls = buyUrls.filter(url => !matchingProducts.some(p => p.buyUrl === url));

    // Afișează statistici
    console.log("\n=== Statistici ===");
    console.log(`Total produse în CSV: ${records.length}`);
    console.log(`Produse găsite în baza de date: ${matchingProducts.length}`);
    console.log(`Produse actualizate la categoria Shoes: ${productsToUpdate.length}`);
    console.log(`Produse deja în categoria Shoes: ${alreadyInShoesCategory}`);
    console.log(`Produse negăsite în baza de date: ${notFoundUrls.length}`);

    if (notFoundUrls.length > 0) {
      console.log("\nPrimele 5 URL-uri negăsite:");
      notFoundUrls.slice(0, 5).forEach(url => {
        const record = records.find(r => r.buyUrl === url);
        if (record) {
          console.log(`- ${record.title}: ${url}`);
        }
      });
    }

  } catch (error) {
    console.error("A apărut o eroare în timpul procesului:", error);
  }
}

// Rulează scriptul
fixShoesFromCSV()
  .then(() => {
    console.log("Script finalizat.");
    process.exit(0);
  })
  .catch(error => {
    console.error("Eroare la rularea scriptului:", error);
    process.exit(1);
  });

