    }
  }

  @keyframes pulseShadow {
    0% {
      text-shadow: 0 0 15px rgba(0, 189, 255, 0.9), 0 0 25px rgba(0, 189, 255, 0.7), 0 0 35px rgba(0, 189, 255, 0.5);
    }
    100% {
      text-shadow: 0 0 20px rgba(0, 189, 255, 1), 0 0 30px rgba(0, 189, 255, 0.8), 0 0 40px rgba(0, 189, 255, 0.6);
    }
  }

  .glow-button {
    position: relative;
    overflow: hidden;
    z-index: 1;
    box-shadow: 0 0 20px rgba(0, 189, 255, 0.7);
    border: 1px solid rgba(0, 189, 255, 0.5);
    text-shadow: 0 0 8px rgba(255, 255, 255, 0.7);
    animation: buttonGlow 3s infinite alternate;
  }

  @keyframes buttonGlow {
    0% {
      box-shadow: 0 0 20px rgba(0, 189, 255, 0.7);
    }
    100% {
      box-shadow: 0 0 30px rgba(0, 189, 255, 0.9);
    }
  }

  .glow-button:after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(255, 255, 255, 0.2);
    transform: translateX(-100%);
    transition: transform 0.6s;
    z-index: -1;
  }

  .glow-button:hover:after {
    transform: translateX(0);
  }

  .glow-button:hover {
    box-shadow: 0 0 35px rgba(0, 189, 255, 1);
    border: 1px solid rgba(0, 189, 255, 0.8);
  }

  /* Card effects */
  .card-zoom:hover img {
    transform: scale(1.05);
    transition: transform 0.3s ease-in-out;
  }

  .product-image {
    transition: transform 0.3s ease-in-out;
  }

  .glow-card {
    box-shadow: 0 5px 25px rgba(0, 189, 255, 0.6);
    transition: all 0.3s ease;
    border: 2px solid rgba(0, 189, 255, 0.4);
    outline: 1px solid rgba(0, 189, 255, 0.6);
    outline-offset: 3px;
    background: rgba(10, 10, 15, 0.9);
    backdrop-filter: blur(10px);
    animation: cardGlow 4s infinite alternate;
  }

  @keyframes cardGlow {
    0% {
      box-shadow: 0 5px 25px rgba(0, 189, 255, 0.6);
    }
    100% {
      box-shadow: 0 5px 35px rgba(0, 189, 255, 0.9);
    }
  }

  .glow-card:hover {
    box-shadow: 0 8px 40px rgba(0, 189, 255, 1);
    transform: translateY(-5px);
    border: 2px solid rgba(0, 189, 255, 0.7);
    outline: 1px solid rgba(0, 189, 255, 0.9);
    outline-offset: 4px;
  }

  /* Animations */
  .fade-in {
    animation: fadeIn 0.8s ease-in-out forwards;
  }

  .slide-up {
    animation: slideUp 0.8s ease-in-out forwards;
  }

  .slide-in-right {
    animation: slideInRight 0.8s ease-in-out forwards;
  }

  .slide-in-left {
    animation: slideInLeft 0.8s ease-in-out forwards;
  }

  .pulse-slow {
    animation: pulseSlow 3s infinite;
  }

  /* Slick carousel customization */
  .slick-dots li button:before {
    color: theme('colors.primary.DEFAULT') !important;
  }

  .slick-dots li.slick-active button:before {
    color: theme('colors.primary.DEFAULT') !important;
    opacity: 1;
  }

  .slick-prev:before, .slick-next:before {
    color: theme('colors.primary.DEFAULT') !important;
  }
}

/* Keyframes */
@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

@keyframes slideUp {
  from {
    opacity: 0;
    transform: translateY(40px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideInRight {
  from {
    opacity: 0;
    transform: translateX(40px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes slideInLeft {
  from {
    opacity: 0;
    transform: translateX(-40px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes pulseSlow {
  0% {
    transform: scale(1);
    opacity: 0.8;
  }
  50% {
    transform: scale(1.08);
    opacity: 0.5;
  }
  100% {
    transform: scale(1);
    opacity: 0.8;
  }
}

@keyframes bgZoom {
  0% {
    transform: scale(1);
  }
  100% {
    transform: scale(1.1);
  }
}

