            onClick={() => setLocation('/')}
          >
            Înapoi la pagina principală
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex flex-col md:flex-row gap-8">
        {/* Imaginea produsului */}
        <div className="md:w-2/5">
